import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Rating from '@mui/material/Rating';
import "./Chatbox.css";
// eslint-disable-next-line
import {
  Box,
  Card,
  CardContent,
  CardHeader,
  Typography,
  Button,
  IconButton,
  Avatar,
  Grid,
} from '@material-ui/core';
import { Close as CloseIcon, Phone as PhoneIcon, Chat as ChatIcon } from '@material-ui/icons';
const useStyles = makeStyles((theme) => ({
  card: {
    borderRadius: 8,
    backgroundColor: '#93e2bb',
    position: 'relative',
    maxWidth: '300px',
    margin: 'auto',
    height: '210',
  },
  modalBody: {
    maxHeight: 400,
    overflowY: 'scroll',
  },
  avatar: {
    width: theme.spacing(8),
    height: theme.spacing(8),
    marginRight: theme.spacing(2),
    border: '3px solid #000',
  },
  followButton: {
    marginRight: theme.spacing(1),
  },
  closeButton: {
    position: 'absolute',
    top: theme.spacing(1),
    right: theme.spacing(1),
  },
  rating: {
    display: 'flex',
    alignItems: 'center',
    color: '#1B7B2C',
    position: 'relative',
  },
  chatButton: {
    marginTop: theme.spacing(1),
  },
  hireButton: {
    borderRadius: 8,
    marginTop:10,
  },
  name: {
    fontFamily: 'Lorem' ,
    fontWeight: 'bold',
    fontSize:20,
  },
}));

export default function Bidprofile({ data }) {
  const classes = useStyles();
  const [isCardVisible, setCardVisible] = useState(true);
  const handleCloseCard = () => {
    setCardVisible(false);
  };

  return (
    <>
      {isCardVisible && (
        <Card className={classes.card}>
          <CardHeader title={data.title} titleTypographyProps={{ variant: 'h6' }} />
          <CardContent>
            <Grid container direction="row" justifyContent="flex-start" alignItems="flex-start" spacing={2}>
              <Grid item  sm={3}>
                <Box display="flex" alignItems="center" justifyContent="center">
                  <Avatar className={classes.avatar} src={data.profileImage} alt="User Avatar" />
                </Box>
              </Grid>
              <Grid item  sm={6}>
                <Box display="flex" alignItems="center" justifyContent="space-between" mb={3}>
                <Box display="flex" alignItems="center" justifyContent="center" mb={2}>
                  <Typography variant="body1"  className={classes.name} component="h1">
                    {data.username}
                  </Typography>
                </Box>
                  <Box className={classes.rating}>
                    <Rating name="read-only" value={data.rating} precision={0.1} readOnly />
                  </Box>
                </Box>
              </Grid>
              <Grid container direction="column" justifyContent="center" alignItems="flex-start"item  sm={9}>
                <Box display="flex" alignItems="center" justifyContent="space-between" mb={3}>
                  <Typography variant="subtitle1" fontWeight="bold">
                    Rs {data.price}
                  </Typography>
                  </Box>
                  <Box display="flex" alignItems="center" justifyContent="space-between" mb={3}>
                  <IconButton color="primary" size="small" className={classes.chatButton}>
                    <ChatIcon />
                  </IconButton>
                  <IconButton color="primary" size="small">
                    <PhoneIcon />
                  </IconButton>
                </Box>
              </Grid>
            </Grid>
            <Button  className={classes.hireButton} variant="contained" color="primary" fullWidth size="large">
              Hire!
            </Button>
          </CardContent>
          <IconButton className={classes.closeButton} onClick={handleCloseCard} aria-label="Close">
            <CloseIcon />
          </IconButton>
        </Card>
      )}
    </>
  );
}
