import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Rating from '@mui/material/Rating';
import {
  Card,
  CardContent,
  CardHeader,
  Typography,
  Button,
  IconButton,
  Avatar,
  Grid,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
} from '@material-ui/core';
import { Close as CloseIcon, Phone as PhoneIcon, Chat as ChatIcon } from '@material-ui/icons';


const useStyles = makeStyles((theme) => ({
  card: {
    borderRadius: 8,
    backgroundColor: '#93e2bb',
    position: 'relative',
    maxWidth: '500px',
    margin: 'auto',
    [theme.breakpoints.up('md')]: {
      maxWidth: '380px',
    },
    height: '300px',
  },
  modalBody: {
    maxHeight: 400,
    overflowY: 'scroll',
  },
  avatar: {
    width: theme.spacing(8),
    height: theme.spacing(8),
    marginRight: theme.spacing(2),
    border: '3px solid #000',
  },
  followButton: {
    marginRight: theme.spacing(1),
  },
  closeButton: {
    position: 'absolute',
    top: theme.spacing(1),
    right: theme.spacing(1),
  },
  rating: {
    display: 'flex',
    alignItems: 'center',
    color: '#1B7B2C',
    position: 'relative',
  },
  chatButton: {
    marginTop: theme.spacing(1),
  },
  hireButton: {
    borderRadius: 8,
    marginTop: 10,
  },
  name: {
    fontFamily: 'Lorem',
    fontWeight: 'bold',
    fontSize: 20,
  },
}));

export default function Bidprofile({ data }) {
  const classes = useStyles();
  const [isCardVisible, setCardVisible] = useState(true);
  const [isDialogOpen, setDialogOpen] = useState(false);

  const handleCloseCard = () => {
    setCardVisible(false);
  };

  const handleCall = () => {
    setDialogOpen(true);
  };

  const handleCloseDialog = () => {
    setDialogOpen(false);
  };

  return (
    <>
      {isCardVisible && (
        <Card className={classes.card}>
          <CardHeader title={data.title} titleTypographyProps={{ variant: 'h6' }} />
          <CardContent>
            <Grid container direction="row" justifyContent="flex-start" alignItems="flex-start" spacing={2}>
              <Grid item sm={3}>
                <Avatar className={classes.avatar} src={data.profileImage} alt="User Avatar" />
              </Grid>
              <Grid item sm={6}>
                <div>
                  <Typography variant="body1" className={classes.name} component="h1">
                    {data.username}
                  </Typography>
                  <Rating name="read-only" value={data.rating} precision={0.1} readOnly />
                </div>
              </Grid>
              <Grid item sm={3}>
                <IconButton color="primary" size="small" className={classes.chatButton}>
                  <ChatIcon />
                </IconButton>
                <IconButton color="primary" size="small" onClick={handleCall}>
                  <PhoneIcon />
                </IconButton>
              </Grid>
            </Grid>
            <Button className={classes.hireButton} variant="contained" color="primary" fullWidth size="large">
              Hire!
            </Button>
          </CardContent>
          <IconButton className={classes.closeButton} onClick={handleCloseCard} aria-label="Close">
            <CloseIcon />
          </IconButton>
        </Card>
      )}

      <Dialog open={isDialogOpen} onClose={handleCloseDialog} aria-labelledby="responsive-dialog-title">
        <DialogTitle id="responsive-dialog-title">Phone Number</DialogTitle>
        <DialogContent>
          <DialogContentText>
            You can contact the Tasker by calling the following number:
            <Typography variant="h6" align="center" color="primary">
              {data.phoneNumber}
            </Typography>
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button autoFocus onClick={handleCloseDialog} color="primary">
            Close
          </Button>
          <Button onClick={() => (window.location.href = `tel:${data.phoneNumber}`)} color="primary" autoFocus>
            Call
          </Button>
        </DialogActions>
      </Dialog>
    </>
  );
}
